INSERT INTO `courses` (`id`, `name`, `price`, `description`, `price_sale`, `course_code`, `time`, `number_lesson`, `image`, `description_full`, `created_at`, `updated_at`) VALUES
	(10, 'Xây dựng website hoàn chỉnh với Laravel PHP Framework', '350000', 'Khóa Học chuyên về Laravel PHP Framework - Hướng dẫn chi tiết nhất cách xây dựng Website hoàn chỉnh với ngôn ngữ PHP thông qua việc sử dụng Framework PHP mạnh mẽ nhất hiện nay', '200000', '1', '04 giờ 17 phút', '52', 'https://www.techcronus.com/wp-content/uploads/2019/06/Laravel-best-PHP-Framework-1568x1045.jpg', '<h3>Bạn sẽ học được g&igrave;</h3>\r\n\r\n<p>Tạo ứng dụng web theo kiến tr&uacute;c MVC: Ứng dụng tinh gọn, dễ d&agrave;ng tuỳ biến v&agrave; bảo tr&igrave;</p>\r\n\r\n<p>Thực hiện điều hướng trang web th&ocirc;ng qua route của Laravel</p>\r\n\r\n<p>Tương t&aacute;c với cơ sở dữ liệu th&ocirc;ng qua hệ thống ORM c&oacute; sẵn của Laravel</p>\r\n\r\n<p>Tương t&aacute;c với cơ sở dữ liệu bằng c&aacute;ch sử dụng lệnh SQL&nbsp;</p>\r\n\r\n<p>X&acirc;y dựng website với to&agrave;n bộ c&aacute;c thao t&aacute;c Th&ecirc;m, Sửa, Xo&aacute;, Truy Vấn&nbsp;</p>\r\n\r\n<p>Thiết lập v&agrave; x&acirc;y dựng kiến tr&uacute;c cơ sở dữ liệu th&ocirc;ng qua c&aacute;c file PHP.</p>\r\n\r\n<p>Tạo layout chung cho ứng dụng website</p>\r\n\r\n<p>Từ l&agrave;m một website ứng dụng ToDoList từ đầu tới cuối với đầy đủ t&iacute;nh năng thao t&aacute;c với cơ sở dữ liệu</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Giới thiệu kh&oacute;a học</h2>\r\n\r\n<p><strong>C&oacute; phải bạn đang muốn&nbsp;học c&ocirc;ng nghệ th&ocirc;ng tin để:</strong></p>\r\n\r\n<p>- Tự tay x&acirc;y dựng một trang web bằng ng&ocirc;n ngữ PHP hỗ trợ đắc lực nhất cho c&ocirc;ng việc kinh doanh b&aacute;n h&agrave;ng của m&igrave;nh?</p>\r\n\r\n<p>- Giải quyết c&aacute;c&nbsp;băn khoăn kh&ocirc;ng biết n&ecirc;n lựa chọn học ng&ocirc;n ngữ lập tr&igrave;nh n&agrave;o, PHP thuần hay Framework? V&agrave; nếu lựa chọn Framework th&igrave; n&ecirc;n lựa chọn c&aacute;i n&agrave;o?</p>\r\n\r\n<p>- Laravel so với Codeiniter th&igrave;&nbsp;c&aacute;i n&agrave;o tối ưu hơn, bảo mật cao hơn v&agrave; hỗ trợ nhiều hơn?</p>\r\n\r\n<p>Nếu bạn vẫn đang đi t&igrave;m c&acirc;u trả lời cho 3 c&acirc;u hỏi lớn tr&ecirc;n, bạn n&ecirc;n lựa chọn x&acirc;u dựng v&agrave; ph&aacute;t triển web với Laravel PHP.</p>\r\n\r\n<h3>Về kh&oacute;a học&nbsp;<strong>&quot;X&acirc;y dựng website ho&agrave;n chỉnh với Laravel PHP Framework&quot;</strong></h3>\r\n\r\n<p>Đ&acirc;y l&agrave; kh&oacute;a học trực tuyến chuy&ecirc;n về Laravel PHP Framework&nbsp;được x&acirc;y dựng v&agrave; hệ thống từ những kiến thức nền tảng cơ bản nhất đến những vấn đề chuy&ecirc;n s&acirc;u nhất, sẽ hướng dẫn bạn ph&aacute;t triển một website phần backend từ đầu tới cuối với ng&ocirc;n ngữ PHP th&ocirc;ng qua việc sử dụng Framework PHP mạnh mẽ nhất b&acirc;y giờ l&agrave; Laravel 5.3</p>\r\n\r\n<p>Kho&aacute; học được thiết kế theo phương ph&aacute;p Learning by Doing. Học th&ocirc;ng qua thực h&agrave;nh.&nbsp;</p>\r\n\r\n<p>Th&ocirc;ng qua những v&iacute; dụ những b&agrave;i tập v&agrave; đặc biệt l&agrave; th&ocirc;ng qua dự &aacute;n cuối m&ocirc;n, bạn sẽ hiểu v&agrave; l&agrave;m được những việc sau:</p>\r\n\r\n<ul>\r\n	<li>X&acirc;y dựng ứng dụng web ho&agrave;n chỉnh c&oacute; t&iacute;nh tương t&aacute;c với dữ liệu bằng PHP</li>\r\n	<li>L&agrave;m việc với Laravel Framework mới nhất</li>\r\n	<li>X&acirc;y dựng theo m&ocirc; h&igrave;nh MVC</li>\r\n	<li>Sử dụng Eloquent ORM</li>\r\n	<li>Sử dụng SQL</li>\r\n	<li>Thiết lập template cho website với Blade Template</li>\r\n	<li>X&acirc;y dựng form để thu thập dữ liệu</li>\r\n	<li>Hiển thị dữ liệu</li>\r\n	<li>T&igrave;m kiếm dữ liệu</li>\r\n	<li>Cập nhật v&agrave; xo&aacute; dữ liệu</li>\r\n	<li>Định hướng trang web với Route</li>\r\n</ul>\r\n\r\n<p>Kết th&uacute;c kho&aacute; học lập tr&igrave;nh web, nếu bạn l&agrave;m được project của dự &aacute;n, th&igrave; t&ocirc;i tin l&agrave; bạn đ&atilde; sẵn s&agrave;ng để l&agrave;m c&ocirc;ng việc của 1 developer PHP ở mức độ th&agrave;nh thạo, c&oacute; thể tự tin nhận c&aacute;c dự &aacute;n mới cho bản th&acirc;n hoặc ứng tuyển v&agrave;o vị tr&iacute; developer PHP của bất kỳ c&ocirc;ng ty, doanh nghiệp n&agrave;o. Bạn h&atilde;y tự tin để bắt đầu một c&ocirc;ng việc mới nh&eacute;!</p>\r\n\r\n<p>Tham gia ngay&nbsp;kh&oacute;a học&nbsp;<strong>&quot;X&acirc;y dựng website ho&agrave;n chỉnh với Laravel PHP Framework&quot;</strong>&nbsp;- kh&oacute;a học d&agrave;nh cho bất cứ ai muốn tự tay x&acirc;y dựng ho&agrave;n thiện một website ho&agrave;n chỉnh ngay tại nh&agrave; một c&aacute;ch b&agrave;i bản, đầy đủ v&agrave; nhanh ch&oacute;ng nhất!</p>', '2021-02-26 12:10:07', '2021-02-27 09:00:19'),
	(11, 'Toàn tập ngôn ngữ lập trình C#', '400000', 'Học lập trình C# bài bản chi tiết nhất tại nhà. Khóa học à cẩm nang đầy đủ về C# cung cấp trọn bộ kiến thức cơ bản - có thể tạo ra một ứng dụng C# hoàn chỉnh', '150000', '2', '19 giờ 46 phút', '44', 'https://static.unica.vn/upload/images/2019/04/hoc-toan-tap-ngon-ngu-lap-trinh-c_m_1555637351.jpg', '<h3>Bạn sẽ học được g&igrave;</h3>\r\n\r\n<p>C&oacute; được to&agrave;n bộ kiến thức về ng&ocirc;n ngữ lập tr&igrave;nh C# từ cơ bản đến chuy&ecirc;n s&acirc;u nhất để c&oacute; thể tự tạo ra đucợ một ứng dụng ho&agrave;n chỉnh từ C#</p>\r\n\r\n<p>Nắm được những nội dung ch&iacute;nh trong ng&ocirc;n ngữ C#: kiến thức về h&agrave;m, cấu tr&uacute;c trong C#, debug, xử l&yacute; chuỗi v&agrave;&nbsp;mảng, x&acirc;y dựng một project thực tế...&nbsp;</p>\r\n\r\n<p>C&oacute; được ki&ecirc;́n thức to&agrave;n diện đầy đủ về ng&ocirc;n ngữ lập tr&igrave;nh C#</p>\r\n\r\n<p>C&oacute; khả năng ph&aacute;t triển phần mềm tương t&aacute;c cơ sở dữ liệu bằng C#</p>\r\n\r\n<p>X&acirc;y dựng được kiến tr&uacute;c đa tầng trong C#, lập tr&igrave;nh win form cơ bản trong C#</p>\r\n\r\n<p>L&agrave; cơ sở để lập tr&igrave;nh di động đa nền tảng với Xamarin...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Giới thiệu kh&oacute;a học</h2>\r\n\r\n<h3><strong>Ng&ocirc;n ngữ lập tr&igrave;nh C#</strong></h3>\r\n\r\n<p>&nbsp; &nbsp; Hiện nay ng&ocirc;n ngữ lập tr&igrave;nh C# l&agrave; một trong những ng&ocirc;n ngữ được rất nhiều người sử dụng trong c&ocirc;ng việc lập tr&igrave;nh của m&igrave;nh. Ch&uacute;ng c&oacute; cấu tr&uacute;c r&otilde; r&agrave;ng, dễ hiểu v&agrave; dễ học. Ng&ocirc;n ngữ lập tr&igrave;nh C# thừa hưởng những ưu việt từ ng&ocirc;n ngữ lập tr&igrave;nh Java, C v&agrave; C++ cũng như c&oacute; khả năng khắc phục được những điểm hạn chế của những ng&ocirc;n ngữ tr&ecirc;n, điều n&agrave;y đ&atilde; tạo n&ecirc;n một ng&ocirc;n ngữ C# với t&iacute;nh ưu việt gần như ho&agrave;n hảo nhất.</p>\r\n\r\n<p>&nbsp; &nbsp; V&agrave; nếu bạn l&agrave; người đang đi t&igrave;m cho m&igrave;nh một lộ tr&igrave;nh học ng&ocirc;n ngữ lập tr&igrave;nh C# một c&aacute;ch b&agrave;i bản, tối ưu chi ph&iacute; v&agrave; linh động thời gian học nhất th&igrave; bạn kh&ocirc;ng n&ecirc;n bỏ qua kh&oacute;a học&nbsp;<strong>&quot;To&agrave;n tập ng&ocirc;n ngữ lập tr&igrave;nh C#&quot;</strong>&nbsp;của giảng vi&ecirc;n đ&agrave;o tạo Trần Duy Thanh.</p>\r\n\r\n<p>&nbsp; &nbsp; Kh&oacute;a học được&nbsp;xem l&agrave; cuốn cẩm nang cần thiết d&agrave;nh cho những ai đang c&oacute; nhu cầu học lập tr&igrave;nh C#, từ những kh&aacute;i niệm cơ bản đến c&aacute;c b&agrave;i học lập tr&igrave;nh C # cụ thể c&oacute; t&iacute;nh ứng dụng cao. Kh&oacute;a học&nbsp;sẽ tr&igrave;nh b&agrave;y c&aacute;c kiến thức về C#, đ&acirc;y l&agrave; ng&ocirc;n ngữ lập tr&igrave;nh hướng đối tượng được ph&aacute;t triển bởi Microsoft, được x&acirc;y dựng dựa tr&ecirc;n C++ v&agrave; Java.</p>\r\n\r\n<h3><strong>Kh&oacute;a học cung c&acirc;́p trọn b&ocirc;̣ ki&ecirc;́n thức cơ bản của l&acirc;̣p tr&igrave;nh C#&nbsp;để&nbsp;học vi&ecirc;n:</strong></h3>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ C&oacute; th&ecirc;̉ tạo ra m&ocirc;̣t ứng dụng C# ho&agrave;n chỉnh sau khi ho&agrave;n th&agrave;nh kh&oacute;a học</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Cung c&acirc;́p những cơ h&ocirc;̣i thực h&agrave;nh tạo ứng dụng C# ngay trong qu&aacute; tr&igrave;nh học</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Sau khi ho&agrave;n th&agrave;nh kh&oacute;a học, c&oacute; th&ecirc;̉ xin việc v&agrave;o c&aacute;c c&ocirc;ng ty sử dụng c&ocirc;ng nghệ .Net</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Học vi&ecirc;n c&oacute; thể tiếp tục học C# n&acirc;ng cao với phần tương t&aacute;c cơ sở dữ liệu</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Kh&oacute;a họ̣c sử dụng những c&ocirc;ng cụ, Framework mới nh&acirc;́t.</p>\r\n\r\n<h3><strong>Kh&oacute;a học ph&ugrave; hợp với ai?</strong></h3>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; ⭕ Học sinh mới tốt nghiệp THPT, muốn học một ng&ocirc;n ngữ lập tr&igrave;nh mới định hướng chuẩn bị cho nghề nghiệp về sau</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; ⭕ Sinh vi&ecirc;n, học vi&ecirc;n Đại học, cao đẳng mới nhập trường nhất l&agrave; c&aacute;c bạn thuộc chuy&ecirc;n ng&agrave;nh kỹ thuật m&aacute;y t&iacute;nh, c&ocirc;ng nghệ th&ocirc;ng tin... muốn học c&ocirc;ng nghệ th&ocirc;ng tin online&nbsp;chuy&ecirc;n s&acirc;u về&nbsp;ng&ocirc;n ngữ lập tr&igrave;nh C# phục vụ cho việc học tập thuận lợi v&agrave; ứng dụng tốt hơn</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; ⭕ Nh&acirc;n vi&ecirc;n, kỹ sư m&aacute;y t&iacute;nh, kỹ sư CNTT... đ&atilde; đi l&agrave;m cần học C# để trau dồi hơn kiến thức của m&igrave;nh.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; ⭕ Bất cứ ai mới bắt đầu học lập tr&igrave;nh để t&igrave;m th&ecirc;m cơ hội nghề nghiệp thu nhập kh&aacute; trở l&ecirc;n.</p>\r\n\r\n<p>C&ograve;n chần chờ g&igrave; nữa m&agrave; kh&ocirc;ng đăng k&yacute; ngay kh&oacute;a học<strong>&quot;To&agrave;n tập ng&ocirc;n ngữ lập tr&igrave;nh C#&quot;&nbsp;</strong>của giảng vi&ecirc;n đ&agrave;o tạo Trần Duy Thanh.</p>', '2021-02-26 12:10:07', '2021-02-27 09:04:06'),
	(12, 'Học HTML5/CSS3, BOOTSTRAP 4 và học cắt Web từ file thiết kế', '250000', 'Khoá học BOOTSTRAP 4, HTML5/CSS3 giúp bạn thành thạo các kỹ năng, phương pháp, hiệu ứng,... cắt Web từ file thiết kế giúp nắm vững kiến thức về CSS, HTML, hiểu rõ Bootstrap và kỹ năng cắt Web với Illustrate, Pix và xử lý nội dung website chuyên nghiệp chỉ với 5 ngày', '100000', '3', '25 giờ 34 phút', '160', 'https://thegioicode.files.wordpress.com/2018/08/d7a3a189492ceb4016ede29fc777748b-1.jpg?w=640', '<h3>Bạn sẽ học được g&igrave;</h3>\r\n\r\n<p>Nắm chắc kiến thức cơ bản về v&agrave; 2 phần mềm cần thiết cho cắt web l&agrave; CSS v&agrave; HTML&nbsp;</p>\r\n\r\n<p>Hiểu r&otilde; về Bootstrap 4, c&aacute;c th&agrave;nh phần của Bootstrap, c&agrave;i đặt cũng như thực h&agrave;nh cắt web với Bootstrap.</p>\r\n\r\n<p>Học cắt web từ file thiết kế Photoshop với c&aacute;c giao diện ILLUSTRATE, pix</p>\r\n\r\n<p>Kỹ năng cắt web với sản phẩm b&aacute;n chạy tr&ecirc;n Themeforest : Thefox</p>\r\n\r\n<p>Thực h&agrave;nh cắt web với 2 phần mềm chuy&ecirc;n dụng CSS v&agrave; HTML&nbsp;tr&ecirc;n c&aacute;c địa chỉ website lớn như: Kenh14.vn, Vietnamnet.vn, Docbao.vn, Marry.vn, Giadinh.net,...</p>\r\n\r\n<p>Hiểu r&otilde; về c&aacute;c thuộc t&iacute;nh trong CSS: thuộc t&iacute;nh Absolute v&agrave; Relative, thuộc t&iacute;nh Fle,..&nbsp; v&agrave; thực h&agrave;nh tr&ecirc;n c&aacute;c web cụ thể, để th&agrave;nh thục c&ocirc;ng cụ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Giới thiệu kh&oacute;a học</h2>\r\n\r\n<p><strong>C&oacute; phải bạn:&nbsp;</strong></p>\r\n\r\n<ul>\r\n	<li>Bạn bắt đầu l&agrave;m quen v&agrave; biết đến cắt web, thiết kế website?</li>\r\n	<li>Bạn y&ecirc;u th&iacute;ch những c&ocirc;ng việc đầy s&aacute;ng tạo, &yacute; tưởng mới lạ cắt, thiết kế web?</li>\r\n	<li>C&ocirc;ng việc của bạn đ&ograve;i hỏi bạn cần biết bắt web, xử l&yacute; th&ocirc;ng tin, tr&igrave;nh b&agrave;y c&aacute;c nội dung tr&ecirc;n web?</li>\r\n	<li>Bạn cần học c&ocirc;ng nghệ th&ocirc;ng tin online&nbsp;để trang bị th&ecirc;m những kiến thức cơ bản nhất về cắt, thiết kế web để phục vụ cho c&ocirc;ng việc của m&igrave;nh, cũng như c&oacute; th&ecirc;m cơ hội thăng tiến, kiếm th&ecirc;m thu nhập cho bản th&acirc;n?</li>\r\n	<li>Bạn mới chỉ biết về cắt, thiết kế web chứ kh&ocirc;ng th&agrave;nh thạo v&agrave; biết c&aacute;c c&ocirc;ng cụ sử dụng trong cắt web?</li>\r\n	<li>Bạn đang t&igrave;m kiếm một&nbsp;kh&oacute;a học lập tr&igrave;nh web&nbsp;ph&ugrave; hợp với m&igrave;nh</li>\r\n</ul>\r\n\r\n<p>Tất cả những kh&oacute; khăn đ&oacute; của bạn sẽ được giải quyết nhanh ch&oacute;ng, ngay lập tức khi bạn tham gia kh&oacute;a. Kh&oacute;a học BOOTSTRAP 4,&nbsp;HTML5/CSS3&nbsp;sẽ gi&uacute;p bạn cải thiện v&agrave; n&acirc;ng cao ngay lập tức tr&igrave;nh độ cắt web, sử dụng c&ocirc;ng cụ t&aacute;c nghiệp tr&ecirc;n website. Đặc biệt, đ&acirc;y l&agrave; kh&oacute;a học d&agrave;nh cho những người mới bắt đầu, cơ bản từ con số 0 về thiết kế website.</p>\r\n\r\n<p>Giảng&nbsp;vi&ecirc;n Nguyễn Đức Việt sẽ c&ugrave;ng với Học vi&ecirc;n Online Unica.vn mang đến cho c&aacute;c bạn một kh&oacute;a học&nbsp;<strong>&ldquo;Học HTML5/CSS3, BOOTSTRAP 4 v&agrave; học cắt Web từ file thiết kế&rdquo;</strong>&nbsp;với 13 phần gi&uacute;p c&aacute;c học vi&ecirc;n biến những kỹ&nbsp;năng cắt web trở n&ecirc;n đơn giản v&ocirc; c&ugrave;ng. Kh&oacute;a học cung cấp cho học vi&ecirc;n đầy đủ những kỹ năng cắt web từ cơ bản đến n&acirc;ng cao. Trong qu&aacute; tr&igrave;nh học, học vi&ecirc;n cũng được thực h&agrave;nh cắt web cụ thể tr&ecirc;n c&aacute;c trang b&aacute;o như: VnExpress, Dantri, Kenh14, Baomoi, Vietnamnet.vn,...</p>\r\n\r\n<p>C&aacute;c bạn sẽ được r&egrave;n luyện th&agrave;nh thục tất cả c&aacute;c kỹ năng cắt web được sử dụng nhiều nhất hiện nay, sử dụng Bootstrap 4 - c&ocirc;ng cụ lập tr&igrave;nh HTML Front End mới nhất đang được nhiều đơn vị web lựa chọn. &nbsp;C&ugrave;ng rất nhiều những t&iacute;nh năng cắt web tối ưu, đảm bảo sản phẩm đẹp c&aacute;c sử dụng font icon v&agrave; l&agrave;m c&aacute;c hiệu ứng hover trực tiếp tr&ecirc;n web,...</p>\r\n\r\n<p>Tr&igrave;nh độ cắt web của bạn sẽ được &ldquo;n&acirc;ng cấp&rdquo; khiến bạn kh&ocirc;ng khỏi bất ngờ. Bạn kh&ocirc;ng chỉ thỏa m&atilde;n được đam m&ecirc; của m&igrave;nh, thỏa sức s&aacute;ng tạo với c&aacute;c hiệu ứng v&agrave; kĩ năng cắt web, m&agrave; c&ograve;n mở th&ecirc;m nhiều cơ hội để thăng tiến trong c&ocirc;ng việc v&agrave; thể hiện tr&igrave;nh độ chuy&ecirc;n m&ocirc;n của m&igrave;nh.</p>\r\n\r\n<p>Vậy c&ograve;n chờ đợi g&igrave; nữa, đăng k&yacute; kh&oacute;a học&nbsp;<strong>&ldquo;Học HTML5/CSS3, BOOTSTRAP 4 v&agrave; học cắt Web từ file thiết kế&rdquo;&nbsp;</strong>của giảng vi&ecirc;n Nguyễn Đức Việt ngay h&ocirc;m nay</p>', '2021-02-26 12:10:07', '2021-02-27 09:06:27'),
	(13, 'Lập trình WinForm với C#: 10 ứng dụng', '140000', 'Khóa học lập trình WinForm không khó vì đã có C#. Khóa học này sẽ giúp bạn có được kiến thức nền tảng về C# và có thể tạo ra ứng dụng C# hoàn chỉnh và ứng dụng nó tốt nhất, hiệu quả nhất', '75000', '4', '20 giờ 28 phút', '42', 'https://static.unica.vn/upload/images/2019/04/lap-trinh-winform-voi-c-ung-dung_1556174870.jpg', '<h3>Bạn sẽ học được g&igrave;</h3>\r\n\r\n<p>C&oacute; được ki&ecirc;́n thức to&agrave;n diện b&agrave;i bản nhất về ng&ocirc;n ngữ lập tr&igrave;nh C#</p>\r\n\r\n<p>C&oacute; khả năng ph&aacute;t triển phần mềm tương t&aacute;c cơ sở dữ liệu bằng C#</p>\r\n\r\n<p>Biết c&aacute;ch lập tr&igrave;nh cơ sở dữ liệu trong C#</p>\r\n\r\n<p>X&acirc;y dựng được kiến tr&uacute;c đa tầng trong C#</p>\r\n\r\n<p>BIết c&aacute;ch đ&oacute;ng g&oacute;i ứng dụng C#&nbsp;</p>\r\n\r\n<p>L&agrave; cơ sở để lập tr&igrave;nh di động đa nền tảng với Xamarin</p>\r\n\r\n<p>Được thực h&agrave;nh một c&aacute;ch chi tiết c&oacute; lời giải tổng hợp to&agrave;n bộ kiến thức C# trong kh&oacute;a học v&oacute;i những b&agrave;i tập s&aacute;t thực tế nhất, t&iacute;nh ứng dụng cao nhất</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Giới thiệu kh&oacute;a học</h2>\r\n\r\n<h3><strong>Nếu bạn l&agrave;:</strong></h3>\r\n\r\n<ul>\r\n	<li>Học sinh / sinh vi&ecirc;n đang theo học c&ocirc;ng nghệ th&ocirc;ng tin&nbsp;muốn t&igrave;m hiểu chuy&ecirc;n&nbsp;s&acirc;u về ng&ocirc;n ngữ lập tr&igrave;nh C# để t&igrave;m việc l&agrave;m th&ecirc;m hay tăng cơ hội nghề nghiệp sau khi ra trường</li>\r\n	<li>Lập tr&igrave;nh vi&ecirc;n: Muốn tiếp cận học ng&ocirc;n ngữ lập tr&igrave;nh mới để n&acirc;ng cao khả năng của m&igrave;nh, mở rộng cơ hội thăng tiến v&agrave; thu nhập?</li>\r\n	<li>V&agrave; bất cứ ai: Y&ecirc;u th&iacute;ch v&agrave; c&oacute; đam m&ecirc; ph&aacute;t triển sự nghiệp trong lĩnh vực lập tr&igrave;nh, đ&atilde; biết một &iacute;t hoặc chưa hề biết g&igrave; về lập tr&igrave;nh?</li>\r\n</ul>\r\n\r\n<p>th&igrave; bạn kh&ocirc;ng n&ecirc;n bỏ lỡ kh&oacute;a học &quot;<strong>Lập tr&igrave;nh WinForm với C#:</strong>&nbsp;<strong>10 ứng dụng</strong>&quot; của giảng vi&ecirc;n&nbsp;<strong>Trần Duy Thanh</strong>. Kh&oacute;a học tại nh&agrave; với lộ tr&igrave;nh học&nbsp;b&agrave;i bản nhất, đầy đủ v&agrave; chi tiết nhất d&agrave;nh cho tất cả mọi người y&ecirc;u th&iacute;ch v&agrave; đam m&ecirc; lập tr&igrave;nh</p>\r\n\r\n<p><strong>Kh&oacute;a học Lập tr&igrave;nh WinForm với C#</strong>&nbsp;10 ứng dụng cung c&acirc;́p trọn b&ocirc;̣ ki&ecirc;́n thức n&acirc;ng cao của l&acirc;̣p tr&igrave;nh C# , học vi&ecirc;n c&oacute; th&ecirc;̉ tạo ra m&ocirc;̣t ứng dụng C# ho&agrave;n chỉnh sau khi ho&agrave;n th&agrave;nh kh&oacute;a học</p>\r\n\r\n<p>Kh&oacute;a học cung c&acirc;́p những cơ h&ocirc;̣i thực h&agrave;nh tạo ứng dụng C# ngay trong qu&aacute; tr&igrave;nh học.</p>\r\n\r\n<h3><strong>Sau khi ho&agrave;n th&agrave;nh kh&oacute;a học, học vi&ecirc;n sẽ c&oacute; thể:&nbsp;</strong></h3>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Kh&oacute;a họ̣c sử dụng những c&ocirc;ng cụ, Framework mới nh&acirc;́t.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ C&aacute;c kiến thức về lập tr&igrave;nh hướng đối tượng trong C#</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Xử l&yacute; chuỗi, mảng, collections</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Sử dụng thuần thục c&aacute;c control khi thiết kế giao diện</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Khả năng gỡ lỗi v&agrave; kiểm so&aacute;t biệt lệ</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Kết nối v&agrave; tương t&aacute;c cơ sở dữ liệu với Micrsoft SQL Server</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Triển khai được m&ocirc; h&igrave;nh đa tầng</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Đ&oacute;ng g&oacute;i được ứng dụng để xuất bản cho kh&aacute;ch h&agrave;ngc</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ C&oacute; th&ecirc;̉ xin việc v&agrave;o c&aacute;c c&ocirc;ng ty sử dụng c&ocirc;ng nghệ .Net</p>\r\n\r\n<h3><strong>Nội dung kh&oacute;a học cụ thể:</strong></h3>\r\n\r\n<p><em>&nbsp; &nbsp; &nbsp;Phần 1: Lập tr&igrave;nh Windows Form n&acirc;ng cao trong C#</em></p>\r\n\r\n<p><em>&nbsp; &nbsp; &nbsp;Phần 2: Xử l&yacute; tập tin trong C#</em></p>\r\n\r\n<p><em>&nbsp; &nbsp; &nbsp;Phần 3: Lập tr&igrave;nh cơ sở dữ liệu trong C#</em></p>\r\n\r\n<p><em>&nbsp; &nbsp; &nbsp;Phần 4: Kiến tr&uacute;c đa tầng trong C#</em></p>\r\n\r\n<p><em>&nbsp; &nbsp; &nbsp;Phần 5: Giới thiệu một số Third party Component</em></p>\r\n\r\n<p><em>&nbsp; &nbsp; &nbsp;Phần 6: Đ&oacute;ng g&oacute;i ứng dụng</em></p>\r\n\r\n<p><em>&nbsp; &nbsp; &nbsp;Phần 7: B&agrave;i tập r&egrave;n luyện tổng hợp- c&oacute; đ&aacute;p &aacute;n</em></p>\r\n\r\n<p><em>&nbsp; &nbsp; &nbsp;Phần 8: B&agrave;i tập r&egrave;n luyện tổng hợp- tự giải</em></p>\r\n\r\n<p>C&ograve;n chần chờ g&igrave; nữa nhanh tay đăng k&yacute; ngay kh&oacute;a học<strong>&quot;Lập tr&igrave;nh WinForm với C#: 10 ứng dụng&quot;</strong>của giảng vi&ecirc;n Trần Duy Thanh nh&eacute;!</p>', '2021-02-26 12:10:07', '2021-02-27 09:08:21'),
	(14, 'Khóa học React js và Redux từ đầu, tạo ứng dụng fullstack với Node JS + React JS', '299000', 'Mục đích mà Facebook nhắm đến trong React là ""Learn Once, Write Anywhere"" - Có nghĩa là sau khi hoàn tất khóa học React JS, ngoài việc bạn thiết kế được layout web, bạn còn sẽ có nền tảng vững chắc để tiếp cận React Native (Mobile App) mà không cần phải mất nhiều thời gian.', '199000', '5', '25 giờ 32 phút', '171', 'https://1.bp.blogspot.com/-Fer07ZV5Ek4/XRm_ui7vdII/AAAAAAAAUyY/N3etZjUCYO4INPR8mf7srQU_5Zux2YHXQCLcBGAs/w1200-h630-p-k-no-nu/aaaa.png', '<p><strong><span style="font-size:14px">Đối tượng học kh&oacute;a học :&nbsp;</span></strong></p>\r\n\r\n<p>- React cực k&igrave; th&acirc;n thiện đối với bạn n&agrave;o đ&atilde; từng học qua JavaScript, CSS hoặc HTML.</p>\r\n\r\n<p>- Sinh vi&ecirc;n học lập tr&igrave;nh web, muốn tối ưu tốc độ xử l&yacute; của website.</p>\r\n\r\n<p>- Những người muốn tiếp cận c&ocirc;ng nghệ lập tr&igrave;nh web bằng React JS nhanh nhất qua b&agrave;i thực h&agrave;nh.</p>\r\n\r\n<p>- Người muốn t&igrave;m hiểu về lập tr&igrave;nh frontend bằng React JS một c&aacute;ch b&agrave;i bản nhưng chưa biết bắt đầu từ đ&acirc;u.</p>\r\n\r\n<p>- C&aacute;c lập tr&igrave;nh vi&ecirc;n front-end đang sử dụng c&aacute;c framework kh&aacute;c, nay muốn n&acirc;ng cao tay nghề th&ocirc;ng qua việc học ReactJS</p>\r\n\r\n<p>- C&aacute;c lập tr&igrave;nh vi&ecirc;n back-end, mobile muốn chuyển sang ph&aacute;t triển c&aacute;c ứng dụng front-end trong thời gian ngắn nhất</p>\r\n\r\n<p>- C&aacute;c lập tr&igrave;nh vi&ecirc;n đang băn khoăn, muốn lựa chọn một framework tốt cho ph&aacute;t triển ứng dụng front-end</p>\r\n\r\n<p><span style="font-size:14px"><strong>&nbsp; Với kho&aacute; học n&agrave;y bạn sẽ&nbsp;</strong></span></p>\r\n\r\n<p>&nbsp;- Nắm được kiến thức b&agrave;i bản từ đầu về thư viện Javascript nổi tiếng ph&aacute;t triển bởi facebook - React JS</p>\r\n\r\n<p>&nbsp;- Nắm được logic hoạt động, c&aacute;c kh&aacute;i niệm, c&aacute;ch thao t&aacute;c, lập tr&igrave;nh chức năng v&agrave; c&aacute;ch kết hợp Redux v&agrave; React JS&nbsp;</p>\r\n\r\n<p>&nbsp;- Học từ đầu về c&aacute;ch thao t&aacute;c với cơ sở dữ liệu&nbsp;PostgreSQL</p>\r\n\r\n<p>&nbsp;- Hiểu c&aacute;ch xử l&yacute; Router trong lập tr&igrave;nh React JS</p>\r\n\r\n<p>&nbsp;- Học c&aacute;ch t&iacute;ch hợp React JS với LocalStorage.&nbsp;</p>\r\n\r\n<p>- Học c&aacute;c t&iacute;ch hợp React JS với Firebase&nbsp;</p>\r\n\r\n<p>&nbsp;- Đưa bạn từng bước đi từ chưa biết g&igrave; cho đến l&uacute;c bạn c&oacute; thể tự tin apply v&agrave;o c&aacute;c c&ocirc;ng ty lập tr&igrave;nh React</p>\r\n\r\n<p>- Kh&oacute;a học sẽ gi&uacute;p bạn c&oacute; thể thiết kế những trang web dựa tr&ecirc;n nền tảng React một c&aacute;ch chuy&ecirc;n nghiệp nhất</p>\r\n\r\n<p>- Ho&agrave;n tất kh&oacute;a học, bạn c&oacute; thể apply v&agrave;o c&aacute;c c&ocirc;ng ty lập tr&igrave;nh Web c&oacute; y&ecirc;u cầu React.</p>', '2021-02-26 12:10:07', '2021-02-27 09:12:53'),
	(15, 'Lập Trình Python Từ Zero - Hero', '200000', 'Khóa học lập trình Python từ Zero đến Hero giúp bạn có được 1 nền tảng vững chắc từ ngôn ngữ Python để xây dựng website , game , application hoặc automation', '100000', '6', '12 giờ 18 phút', '89', 'https://i.ytimg.com/vi/R2Jeg63ySwM/hqdefault.jpg', '<h3>Bạn sẽ học được g&igrave;</h3>\r\n\r\n<p>Kiến thức nền tảng nhất về ng&ocirc;n ngữ lập tr&igrave;nh Python cho đến ứng dụng Python n&acirc;ng cao v&agrave;o c&aacute;c t&igrave;nh huống thực tế</p>\r\n\r\n<p>Tiếp cận v&agrave; l&agrave;m việc v&agrave; thực h&agrave;nh&nbsp;với Python từ con số 0</p>\r\n\r\n<p>Nắm được c&aacute;c kiến thức c&oacute; trong lập tr&igrave;nh Python: biểu thức trong Python, đối tượng&nbsp;String,&nbsp;List, Tuples, Sets v&agrave; Dictionary, h&agrave;m tự định nghĩa, module trong Python...&nbsp;</p>\r\n\r\n<p>X&acirc;y dựng Website, Game, Application hoặc Automation bằng ng&ocirc;n ngữ Python</p>\r\n\r\n<p>Tự &ocirc;n tập v&agrave; n&acirc;ng cao kỹ năng lập tr&igrave;nh Python với c&aacute;c b&agrave;i tập v&agrave; t&igrave;nh huống thực c&oacute; trong qu&aacute; tr&igrave;nh l&agrave;m việc của lập tr&igrave;nh vi&ecirc;n</p>\r\n\r\n<p>Nhanh ch&oacute;ng th&agrave;nh thạo c&aacute;c thao t&aacute;c l&agrave;m việc v&agrave; &aacute;p dụng v&agrave;o c&ocirc;ng việc lập tr&igrave;nh</p>\r\n\r\n<p>Tự tin phỏng vấn v&agrave;o c&aacute;c c&ocirc;ng ty, doanh nghiệp với mức lương cao</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Giới thiệu kh&oacute;a học</h2>\r\n\r\n<p>Thế giới ng&agrave;y nay c&oacute; rất rất nhiều ng&ocirc;n ngữ lập tr&igrave;nh được sử dụng phổ biến, trong đ&oacute; phải kể đến c&aacute;c ng&ocirc;n ngữ&nbsp;C++, C#, Java, Python,&nbsp;PHP,... Đặc biệt, Python đang l&agrave; ng&ocirc;n ngữ lập tr&igrave;nh m&agrave; những người mới&nbsp;<a href="https://unica.vn/course/cong-nghe-thong-tin">học c&ocirc;ng nghệ th&ocirc;ng tin online</a>&nbsp;t&igrave;m học nhiều nhất. Cho đến nay những lập tr&igrave;nh vi&ecirc;n Python vẫn lu&ocirc;n được c&aacute;c nh&agrave; tuyển dụng tại c&aacute;c doanh nghiệp lớn nhỏ chi&ecirc;u mộ với những ưu &aacute;i cực tốt với mức thu nhập xếp v&agrave;o loại cao nhất.</p>\r\n\r\n<p><strong>Những l&yacute; do bạn n&ecirc;n học Python b&ecirc;n cạnh c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh kh&aacute;c:&nbsp;</strong></p>\r\n\r\n<p>✔️L&yacute; do 1: Python l&agrave; ng&ocirc;n ngữ mới v&agrave; dễ học hơn bất k&igrave; ng&ocirc;n ngữ n&agrave;o</p>\r\n\r\n<p>✔️L&yacute; do 2: Python l&agrave; ng&ocirc;n ngữ h&agrave;ng đầu tại c&aacute;c đất nước tr&ecirc;n thế giới nhất l&agrave; Ch&acirc;u &Acirc;u. V&agrave; bạn cũng đ&atilde; biết tương lai Việt Nam sẽ được hội nhập với c&aacute;c nước tr&ecirc;n thế giới vậy cho n&ecirc;n nguồn lực cho m&atilde; nguồn Python l&agrave; cực k&igrave; nhiều.V&igrave; đa phần c&aacute;c nước ch&acirc;u &Acirc;u họ đều s&agrave;i Python thay cho C.</p>\r\n\r\n<p>✔️L&yacute; do 3: Python l&agrave; ng&ocirc;n ngữ gi&uacute;p bạn viết được tr&ecirc;n đa nền tảng n&ecirc;n v&iacute; dụ như lập tr&igrave;nh website , lập tr&igrave;nh di động v&agrave; cả lập tr&igrave;nh game th&igrave; Python đều c&oacute; thể l&agrave;m được.</p>\r\n\r\n<p>✔️L&yacute; do 4: Đảm bảo đ&acirc;y l&agrave; nội dung đầy đủ v&agrave; kỹ c&agrave;ng nhất về ng&ocirc;n ngữ lập tr&igrave;nh Python để từ đ&oacute; bạn c&oacute; thể &aacute;p dụng Python v&agrave;o c&aacute;c c&ocirc;ng việc bạn muốn hướng tới hoặc tạo th&ecirc;m nhiều cơ hội việc l&agrave;m cho bạn.</p>\r\n\r\n<p>H&atilde;y tham gia kh&oacute;a học lập tr&igrave;nh web trực tuyến&nbsp;<strong>&quot;Lập Tr&igrave;nh Python Từ Zero - Hero&quot;</strong>&nbsp;của giảng vi&ecirc;n Vũ Quốc Tuấn để nhanh ch&oacute;ng trở&nbsp;th&agrave;nh chuy&ecirc;n gia lập tr&igrave;nh Python ngay tại nh&agrave;!</p>\r\n\r\n<p><strong>Kh&oacute;a học&nbsp;gồm c&aacute;c nội dung sau:</strong></p>\r\n\r\n<p>Phần 1: C&aacute;c kh&aacute;i niệm cơ bản trong lập tr&igrave;nh Python</p>\r\n\r\n<p>Phần 2: Biểu thức trong lập tr&igrave;nh Python</p>\r\n\r\n<p>Phần 3: Đối tượng String trong lập tr&igrave;nh Python</p>\r\n\r\n<p>Phần 4: Đối tượng List, Tuples, Sets v&agrave; Dictionary</p>\r\n\r\n<p>Phần 5: Thao t&aacute;c với h&agrave;m tự định nghĩa trong lập tr&igrave;nh Python</p>\r\n\r\n<p>Phần 6: Hướng đối tượng v&agrave; thao t&aacute;c với File trong Python</p>\r\n\r\n<p>Phần 7: T&igrave;m hiểu về module v&agrave; quản l&yacute; lỗi trong Python</p>\r\n\r\n<p>Phần 8: Thao t&aacute;c l&agrave;m việc với cơ sở dữ liệu</p>\r\n\r\n<p>Phần 9: T&igrave;m hiểu thao t&aacute;c l&agrave;m việc với mảng qua thư viện Numpy</p>\r\n\r\n<p>Phần 10: Thao t&aacute;c quản l&yacute; dữ liệu với Pandas</p>\r\n\r\n<p>Phần 11: B&agrave;i tập t&igrave;nh huống</p>\r\n\r\n<p>Cờn chờ đợi g&igrave; nữa đăng k&yacute; ngay kh&oacute;a học<strong>&nbsp;&quot;Lập Tr&igrave;nh Python Từ Zero - Hero&quot;</strong></p>', '2021-02-26 12:10:07', '2021-02-27 09:17:16'),
	(16, 'Bắt đầu với ứng dụng website trên nền tảng ASP.NET MVC', '190000', 'Khóa học giúp bạn nắm được quy trình làm việc của ASP.NET MVC trong dự án thực tế, tự tay xây dựng được ứng dụng website sử dụng ASP.NET MVC và xây dựng được website TMĐT', '90000', '7', '06 giờ 35 phút', '73', 'https://i.ytimg.com/vi/GqDuHizSH7U/maxresdefault.jpg', '<h3>Bạn sẽ học được g&igrave;</h3>\r\n\r\n<p>Nhanh ch&oacute;ng nắm vững kiến thức v&agrave; kỹ năng cơ bản nhất về ASP.Net MVC để tạo lập một website&nbsp;</p>\r\n\r\n<p>C&oacute; kiến thức nền tảng để chuẩn bị học c&aacute;c kiến thức cao hơn, chuy&ecirc;n s&acirc;u hơn</p>\r\n\r\n<p>Tự tin x&acirc;y dựng website thương mại điện tử tr&ecirc;n nền tảng ASP.NET MVC</p>\r\n\r\n<p>L&agrave;m nền tảng cho việc nghi&ecirc;n cứu c&aacute;c kỹ thuật v&agrave; c&ocirc;ng nghệ mới của Microsoft</p>\r\n\r\n<p>X&acirc;y dựng ứng dụng quản l&yacute; tr&ecirc;n nền tảng Internet</p>\r\n\r\n<p>Tăng khả năng thăng tiến trong c&ocirc;ng việc</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Giới thiệu kh&oacute;a học</h2>\r\n\r\n<h3><strong>ASP.NET MVC - Một trong những framework l&agrave;m việc tuyệt vời của d&acirc;n lập tr&igrave;nh web</strong></h3>\r\n\r\n<p>M&ocirc; h&igrave;nh MVC (Model - View - Controller) c&oacute; lẽ kh&ocirc;ng c&ograve;n xa lạ g&igrave; với lập tr&igrave;nh vi&ecirc;n. Cộng với đặc điểm của nền tảng ASP.NET MVC đ&oacute; l&agrave; nhẹ, dễ kiểm thử giao diện hơn nhiều so với web forms v&agrave; t&iacute;ch hợp sẵn kh&aacute; nhiều t&iacute;nh năng của ASP.NET, khả năng x&acirc;y dựng web v&agrave; ứng dụng n&acirc;ng cao của web tr&ecirc;n m&ocirc; h&igrave;nh MVC tốt hơn nhiều so với c&aacute;c nền tảng kh&aacute;c.</p>\r\n\r\n<p>V&agrave; nếu bạn l&agrave; người muốn&nbsp;học c&ocirc;ng nghệ th&ocirc;ng tin v&agrave;&nbsp;đang định hướng trở th&agrave;nh một lập tr&igrave;nh vi&ecirc;n chuy&ecirc;n về website, hoặc người bị mất căn bản muốn học lại từ đầu ASP.NET MVC th&igrave;...</p>\r\n\r\n<p><em>CH&Uacute;C MỪNG BẠN, bạn đ&atilde; đến đ&uacute;ng nơi d&agrave;nh ri&ecirc;ng cho bạn! Nơi đ&acirc;y c&oacute; v&ocirc; v&agrave;n những&nbsp;kh&oacute;a học lập tr&igrave;nh web&nbsp;d&agrave;nh cho bạn!</em></p>\r\n\r\n<h3><strong>H&atilde;y tham gia ngay kh&oacute;a học&nbsp;&quot;Bước đầu với ứng dụng website tr&ecirc;n nền tảng ASP.NET MVC&quot;&nbsp;tại Unica!</strong></h3>\r\n\r\n<p>Kho&aacute; học do giảng vi&ecirc;n Trương Minh Tuấn trực tiếp&nbsp;hướng dẫn gi&uacute;p bạn từng bước l&agrave;m việc tr&ecirc;n nền tảng ASP.NET MVC, thực h&agrave;nh Lab l&agrave; ch&iacute;nh, mang đến cho bạn sự th&uacute; vị khi theo d&otilde;i.</p>\r\n\r\n<h3><strong>L&yacute; do v&igrave; sao bạn kh&ocirc;ng thể bỏ qua kh&oacute;a học n&agrave;y?</strong></h3>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Kho&aacute; học c&oacute; t&iacute;nh thực tiễn cao, nội dung hướng đến nền tảng c&ocirc;ng nghệ nhằm phục vụ tốt việc học tập, tự nghi&ecirc;n cứu của học vi&ecirc;n.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Th&ocirc;ng qua kh&oacute;a học ng&ocirc;n ngữ lập tr&igrave;nh, bạn sẽ hiểu được nền tảng c&ocirc;ng nghệ, v&agrave; vận dụng được v&agrave;o dự &aacute;n thực tế</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Đ&acirc;y l&agrave; nền tảng c&ocirc;ng nghệ mới nhất của Microsoft để thiết kế website v&agrave; x&acirc;y dựng c&aacute;c ứng dụng quản l&yacute; tr&ecirc;n nền tảng Internet</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Bạn sẽ nắm vững kiến thức l&agrave;m việc giữa ASP.NET MVC v&agrave; Entity Framework th&ocirc;ng tin truy vấn dữ liệu LINQ.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Biết quy tr&igrave;nh l&agrave;m việc của ASP.NET MVC trong dự &aacute;n thực tế. Từ đ&oacute;, gi&uacute;p bạn tự x&acirc;y dựng được ứng dụng web sử dụng ASP.NET MVC.</p>\r\n\r\n<p>Nhanh tay tham gia kh&oacute;a học&nbsp;<strong>&quot;Bước đầu với ứng dụng website tr&ecirc;n nền tảng ASP.NET MVC&quot;</strong>&nbsp;ngay h&ocirc;m nay</p>', '2021-02-26 12:10:07', '2021-02-27 09:20:10'),
	(17, 'Học lập trình C/C++ TỪ A - Z', '390000', 'Trang bị cho bạn kỹ năng lập trình ngôn ngữ C/C++ từ cơ bản đến nâng cao, được minh họa thông qua các bài tập thực hành thực tế nhất về C/C++', '290000', '8', '15 giờ 38 phút', '99', 'https://static.unica.vn/upload/images/2019/04/hoc-lap-trinh-c-c-a-toi-z-duong-tich-dat_1555574622.jpg', '<h3>Bạn sẽ học được g&igrave;</h3>\r\n\r\n<p>C&oacute; kiến thức nền tảng vững chắc về ng&ocirc;n ngữ C/C++ th&ocirc;ng qua những kiến thức l&yacute; thuyết v&agrave; b&agrave;i tập thực h&agrave;nh</p>\r\n\r\n<p>Sử dụng thư viện STL th&agrave;nh thạo cũng như kĩ thuật lập tr&igrave;nh h&agrave;m.</p>\r\n\r\n<p>Kiến thức lập tr&igrave;nh đủ để x&acirc;y dựng phần mềm từ &yacute; tưởng đến th&agrave;nh phẩm ho&agrave;n thiện&nbsp;</p>\r\n\r\n<p>Nhanh ch&oacute;ng th&agrave;nh thạo ng&ocirc;n ngữ C/C++ với c&aacute;c kiểu dữ liệu mảng, con trỏ, kiểu cấu tr&uacute;c... những kiến thức kh&ocirc;ng thể kh&ocirc;ng biết khi học C/C++</p>\r\n\r\n<p>Biết sử dụng kỹ thuật đệ quy để giải quyết b&agrave;i to&aacute;n phức tạp.</p>\r\n\r\n<p>Thiết kế, x&acirc;y dựng, thử nghiệm v&agrave; gỡ lỗi (design, implement, test, and debug).</p>\r\n\r\n<p>C&oacute; đủ kiến thức nền tảng cơ bản về&nbsp;C/C++ để bạn&nbsp;tiếp tục tự học th&ecirc;m&nbsp;ng&ocirc;n ngữ n&agrave;y hoặc học th&ecirc;m một ng&ocirc;n ngữ lập tr&igrave;nh mới kh&aacute;c</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Giới thiệu kh&oacute;a học</h2>\r\n\r\n<h3>Ng&ocirc;n ngữ lập tr&igrave;nh bậc cao C/C++</h3>\r\n\r\n<p>Ng&ocirc;n ngữ lập tr&igrave;nh C++ được x&acirc;y dựng v&agrave; ph&aacute;t triển từ ng&ocirc;n ngữ C. Ng&ocirc;n ngữ C++ kh&ocirc;ng phải l&agrave; ng&ocirc;n ngữ hướng đối tượng ho&agrave;n to&agrave;n m&agrave; l&agrave; ng&ocirc;n ngữ &ldquo;đa hướng&rdquo;. V&igrave; C++ hỗ trợ cả lập tr&igrave;nh hướng h&agrave;nh động v&agrave; lập tr&igrave;nh hướng đối tượng. N&oacute; l&agrave; một trong những ng&ocirc;n ngữ phổ biến để viết c&aacute;c ứng dụng m&aacute;y t&iacute;nh&nbsp;&ndash; v&agrave; ng&ocirc;n ngữ th&ocirc;ng dụng nhất để lập tr&igrave;nh games. &nbsp;</p>\r\n\r\n<h3>Kh&oacute;a học n&agrave;y ch&iacute;nh l&agrave; d&agrave;nh cho bạn:</h3>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;???? Học sinh đ&atilde; tốt nghiệp PTTH, c&oacute; sự y&ecirc;u th&iacute;ch v&agrave; đam m&ecirc; lập tr&igrave;nh hệ thống, y&ecirc;u th&iacute;ch m&aacute;y t&iacute;nh, c&ocirc;ng nghệ th&ocirc;ng tin, muốn học ngay từ đầu về C/C++ một c&aacute;ch b&agrave;i bản, chi tiết</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;???? Sinh vi&ecirc;n, học vi&ecirc;n c&aacute;c trường đại học chuy&ecirc;n ng&agrave;nh CNTT, khoa học m&aacute;y t&iacute;nh, c&ocirc;ng nghệ phần mềm, bảo mật... đang t&igrave;m kiếm c&aacute;c kho&aacute; học c&ocirc;ng nghệ th&ocirc;ng tin online&nbsp;để&nbsp;học C/C++ chuy&ecirc;n s&acirc;u bằng những b&agrave;i tập thực h&agrave;nh để tăng kiến thức v&agrave; trau dồi hơn kỹ năng lập tr&igrave;nh của m&igrave;nh</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;???? Nh&acirc;n vi&ecirc;n lập tr&igrave;nh x&acirc;y dựng v&agrave; ph&aacute;t triển ứng dụng phần mềm tại c&aacute;c doanh nghiệp, muốn học C/C++ để n&acirc;ng cao hơn kỹ năng lập tr&igrave;nh của bản th&acirc;n, vừa học vừa ứng dụng được ngay v&agrave;o c&ocirc;ng việc.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;???? Một người ho&agrave;n to&agrave;n chưa biết g&igrave; về lập tr&igrave;nh, muốn học ng&ocirc;n ngữ lập tr&igrave;nh C/C++ để mở th&ecirc;m những cơ hội mới cho bản th&acirc;n</p>\r\n\r\n<p><strong>H&atilde;y tham gia ngay kh&oacute;a học &quot;Học&nbsp;C/C++ TỪ A - Z&quot; &nbsp;của giảng vi&ecirc;n Dương T&iacute;ch Đạt!</strong></p>\r\n\r\n<h3>Kh&oacute;a học c&oacute; g&igrave; d&agrave;nh cho bạn?</h3>\r\n\r\n<p>Kh&oacute;a học sẽ&nbsp;trang bị cho học vi&ecirc;n c&aacute;c kỹ năng lập tr&igrave;nh được minh hoạ cụ thể bằng ng&ocirc;n ngữ lập tr&igrave;nh C/C++ từ cơ bản đến n&acirc;ng cao. Kh&oacute;a học bao gồm c&aacute;c kỹ thuật lập tr&igrave;nh tr&ecirc;n c&aacute;c kiểu dữ liệu cơ bản, c&aacute;c ph&aacute;t biểu lựa chọn, c&acirc;u lệnh điều khiển, v&ograve;ng lặp, mảng, con trỏ, kiểu cấu tr&uacute;c. B&ecirc;n cạnh đ&oacute; kh&oacute;a học cũng trang bị cho học vi&ecirc;n kiến thức xử l&yacute; tập tin, c&aacute;ch viết chương tr&igrave;nh theo kiểu lập tr&igrave;nh h&agrave;m...</p>\r\n\r\n<p>Qua kh&oacute;a học&nbsp;<strong>Học lập tr&igrave;nh C/C++ TỪ A - Z</strong>, học vi&ecirc;n c&oacute; thể tự n&acirc;ng cao kỹ năng lập tr&igrave;nh của m&igrave;nh, dễ d&agrave;ng tiếp cận c&aacute;c ng&ocirc;n ngữ cấp cao v&agrave; c&ocirc;ng nghệ mới. Đ&acirc;y l&agrave; kh&oacute;a học tạo tiền đề tốt cho việc tiếp cận phương ph&aacute;p lập tr&igrave;nh hướng đối tượng, một phương ph&aacute;p lập tr&igrave;nh cần phải c&oacute; của một lập tr&igrave;nh vi&ecirc;n.</p>\r\n\r\n<p>Vậy c&ograve;n chờ g&igrave; nữa, h&atilde;y tham gia ngay kh&oacute;a học&nbsp;<strong>&quot;Học&nbsp;C/C++ TỪ A - Z&quot;&nbsp;</strong>th&ocirc;i n&agrave;o!</p>', '2021-02-26 12:10:07', '2021-02-27 09:23:11'),
	(18, 'Lập trình Android từ cơ bản đến thành thạo', '490000', 'Khóa học lập trình Android toàn tập tạo dựng một kiến thức vững chắc để học viên có thể tự vận hành các ứng dụng trên Appstore một cách nhanh chóng', '390000', '9', '29 giờ 50 phút', '104', 'http://hoctainha.online/upload/hinhanh/thumb/lap-trinh-android-tu-co-ban-den-thanh-thao-49.png', '<h3>Bạn sẽ học được g&igrave;</h3>\r\n\r\n<p>C&oacute; được kiến thức nền tảng nhất về ng&ocirc;n ngữ lập tr&igrave;nh Java v&agrave; ứng dụng thực tế với hệ điều h&agrave;nh Android</p>\r\n\r\n<p>Nắm được quy tr&igrave;nh thiết kế ứng dụng Android tr&ecirc;n Android Studio</p>\r\n\r\n<p>Biết c&aacute;ch xử l&yacute; c&aacute;c vấn đề trong qu&aacute; tr&igrave;nh thiết kế ứng dụng Android</p>\r\n\r\n<p>C&oacute; thể kiếm tiền ngay trong khi học nhờ những dự &aacute;n do ch&iacute;nh tay bạn l&agrave;m.</p>\r\n\r\n<p>Kiếm tiền th&ocirc;ng qua c&aacute;c c&aacute;ch chạy quảng c&aacute;o AdMob , Aripush hay StartApp</p>\r\n\r\n<p>Tự x&acirc;y dựng nh&oacute;m để c&oacute; thể nhận dự &aacute;n outsource từ c&aacute;c trang Freelancer</p>\r\n\r\n<p>T&igrave;m hiểu phương thức Random; Animation v&agrave; Game bằng Animation; ListView trong Android...</p>\r\n\r\n<p>C&oacute; thể tự x&acirc;y dựng ứng dụng Clone Mp3.Zing.Vn; Webservice - X&acirc;y dựng cửa h&agrave;ng b&aacute;n quần &aacute;o</p>\r\n\r\n<p>Xin được việc m&agrave; kh&ocirc;ng cần bằng cấp với những kiến thức v&agrave; kỹ năng được cung cấp</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Giới thiệu kh&oacute;a học</h2>\r\n\r\n<p>&nbsp; &nbsp; Hệ điều h&agrave;nh Android&nbsp;hiện nay đang l&agrave; hệ điều h&agrave;nh số một thế giới chiếm tới hơn 85% thị phần di động to&agrave;n cầu. Đ&acirc;y l&agrave; hệ điều h&agrave;nh c&oacute; m&atilde; nguồn do ch&iacute;nh Google ph&aacute;t h&agrave;nh, do đ&oacute; cho đến nay ch&uacute;ng đ&atilde; cho ph&eacute;p c&aacute;c nh&agrave; ph&aacute;t triển, lập tr&igrave;nh vi&ecirc;n điều chỉnh v&agrave; ph&acirc;n phối Android một c&aacute;ch tự do nhất.</p>\r\n\r\n<p>&nbsp; &nbsp; Android t&iacute;ch hợp cho gần như cho tất cả c&aacute;c thiết bị điện tử: điện thoại di động, m&aacute;y t&iacute;nh bảng, smart TV, Smartwatch, ... cho n&ecirc;n những lập tr&igrave;nh vi&ecirc;n Android lu&ocirc;n c&oacute; vai tr&ograve; rất lớn trong việc lập tr&igrave;nh v&agrave; ph&aacute;t triển c&aacute;c &uacute;ng dụng như thế n&agrave;y.</p>\r\n\r\n<p>&nbsp; &nbsp; Với thị trường kh&aacute;t nh&acirc;n lực như hiện nay nghề lập tr&igrave;nh vi&ecirc;n Android đ&atilde; tạo ra v&ocirc; v&agrave;n cơ hội rộng lớn cho c&aacute;c bạn trẻ, nhất l&agrave; đối với những bạn y&ecirc;u th&iacute;ch&nbsp;học c&ocirc;ng nghệ th&ocirc;ng tin, lập tr&igrave;nh v&agrave;&nbsp;y&ecirc;u th&iacute;ch c&ocirc;ng việc ph&acirc;n t&iacute;ch v&agrave; ph&aacute;t triển hệ điều h&agrave;nh Android.</p>\r\n\r\n<p>&nbsp; &nbsp; Nhận thấy nhu cầu đ&oacute;, giảng vi&ecirc;n Vũ Quốc Tuấn kết hợp c&ugrave;ng Unica đ&atilde; cho ra mắt kh&oacute;a học &quot;<strong>Lập tr&igrave;nh Android từ cơ bản đến th&agrave;nh thạo&quot;</strong>&nbsp;cung cấp những kiến thức cần thiết về lập tr&igrave;nh Java v&agrave; c&aacute;c ứng dụng:</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Quy tr&igrave;nh thiết kế ứng dụng Android tr&ecirc;n Android Studio;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ C&aacute;c đối tượng cơ bản trong Android; Đối tượng Form; T&igrave;m hiểu về đối tượng Seekbar</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️&nbsp;Xử l&yacute; c&aacute;c vấn đề về thời gian</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Animation v&agrave; game bằng Animation</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Chạy quảng c&aacute;o Admob v&agrave; Publish ứng dụng...</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ X&acirc;y dựng ứng dụng Clone Mp3.Zing.vn</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ X&acirc;y dựng cửa h&agrave;ng b&aacute;n quần &aacute;o đơn giản ...&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; Kh&oacute;a học &quot;<strong>Lập tr&igrave;nh Android từ cơ bản đến th&agrave;nh thạo&quot;&nbsp;</strong>của giảng vi&ecirc;n Vũ Quốc Tuấn&nbsp;sẽ tạo dựng một kiến thức vững chắc để học vi&ecirc;n c&oacute; thể tự vận h&agrave;nh c&aacute;c ứng dụng tr&ecirc;n Appstore một c&aacute;ch nhanh ch&oacute;ng. Học để l&agrave;m được việc chứ kh&ocirc;ng chỉ l&agrave; l&yacute; thuyết su&ocirc;ng.&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; Trao kiến thức v&agrave; kỹ năng lập tr&igrave;nh cơ bản nhất để c&aacute;c bạn kh&ocirc;ng bị ngỡ ng&agrave;ng cũng như dễ d&agrave;ng t&igrave;m ra c&aacute;i gốc của vấn đề, từ đ&oacute; đưa ra hướng giải quyết v&agrave; ph&aacute;t triển; Trở th&agrave;nh một chuy&ecirc;n vi&ecirc;n lập tr&igrave;nh ứng dụng di động chuy&ecirc;n nghiệp với c&aacute;c ứng dụng thực tế trong kho&aacute; học ng&ocirc;n ngữ lập tr&igrave;nh</p>\r\n\r\n<p>&nbsp; &nbsp; Tham gia kh&oacute;a học&nbsp;&quot;<strong>Lập tr&igrave;nh Android từ cơ bản đến th&agrave;nh thạo&quot;</strong>&nbsp;ngay h&ocirc;m nay để nhanh ch&oacute;ng l&agrave;m chủ được Java v&agrave;&nbsp;sớm trở th&agrave;nh một chuy&ecirc;n vi&ecirc;n lập tr&igrave;nh ứng dụng Android t&agrave;i năng nhất!</p>', '2021-02-26 12:10:07', '2021-02-27 09:24:38'),
	(19, 'C# toàn tập căn bản – chuyên sâu – Dự án Quản lý hệ thống chấm công – nhân sự', '390000', 'Khoá học cung cấp đầy đủ cho các bạn từ kiến thức nền tảng nhất đến tất cả các kiến thức nâng cao của ngôn ngữ C#, từ phân tích yêu cầu đến phân tích các chức năng qua các lược đồ quan hệ qua đó bạn sẽ được giảng viên hướng dẫn cách thiết kế giao diện phần mềm và xây dựng các chức năng một cách chi tiết. Khóa học được xây dựng trên nền tảng .net FrameWork 4.6.1 của Microsoft lập trình Window Form hướng đến giao diện metro UI mang cảm hứng mới mẽ. Các tính năng tiêu biểu của phần mềm: Chấm công, tính lương, in báo cáo, backup, restore đóng gói … hãy tham gia khóa học ngay hôm nay để được nhận mức giá ưu đãi các bạn nhé.', '290000', '10', '34 giờ 45 phút', '58', 'https://gametus.com/wp-content/uploads/2018/05/lap-trinh-c-.jpg', '<h3><strong><span style="font-size:14px">Học vi&ecirc;n sẽ học được g&igrave;&nbsp;?</span></strong></h3>\r\n\r\n<ul>\r\n	<li>&nbsp;Nắm vững kiến thức lập tr&igrave;nh C# Windown Form.</li>\r\n	<li>&nbsp;Ứng dụng x&acirc;y dựng được nhiều hệ thống quản l&yacute; kh&aacute;c tượng tự tr&ecirc;n nền tảng Window Form C#.</li>\r\n	<li>&nbsp;Sử dụng thuần thục Entity Framework c&ugrave;ng 1 số truy vấn linQ.</li>\r\n</ul>', '2021-02-26 12:10:07', '2021-02-27 09:27:43'),
	(20, 'Khóa học Photoshop từ cơ bản đến nâng cao', '390000', 'Khóa học photoshop này sẽ giúp bạn chinh phục và làm chủ Photoshop để tạo nên những bức ảnh đẹp, thiết kế banner, thiết kế giao diện website.', '290000', '11', '05 giờ 18 phút', '50', 'https://i.ytimg.com/vi/i0m3Zqe6U6U/maxresdefault.jpg', '<h3>Bạn sẽ học được g&igrave;</h3>\r\n\r\n<p>Ứng dụng ho&agrave;n to&agrave;n những c&ocirc;ng cụ trong Photoshop từ cơ bản đến n&acirc;ng cao.</p>\r\n\r\n<p>Ứng dụng chỉnh sửa, cắt gh&eacute;p h&igrave;nh ảnh bằng Photoshop .</p>\r\n\r\n<p>Chỉnh sửa m&agrave;u sắc h&igrave;nh ảnh, kỹ thuật blend m&agrave;u trong bức ảnh.</p>\r\n\r\n<p>Thiết kế những mẫu banner s&aacute;ng tạo v&agrave; chuy&ecirc;n nghiệp trong quảng c&aacute;o.</p>\r\n\r\n<p>Thiết kế 1 giao diện website ho&agrave;n chỉnh.</p>\r\n\r\n<p>Bi&ecirc;n tập h&igrave;nh ảnh, s&aacute;ng tạo nội dung h&igrave;nh ảnh bằng Photoshop.</p>\r\n\r\n<p>Xử l&yacute; v&agrave; hiệu chỉnh kỹ xảo h&igrave;nh ảnh n&acirc;ng cao manuapltion.</p>\r\n\r\n<p>Tự tay l&agrave;m 1 Album ảnh cưới đặc sắc... V&agrave; nhiều hơn thế nữa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Giới thiệu kh&oacute;a học</h2>\r\n\r\n<p dir="ltr"><em>Bạn l&agrave; người đam m&ecirc; thiết kế đồ họa, bạn muốn tự tay m&igrave;nh xử l&yacute; chỉnh sửa v&agrave; thiết kế những tấm h&igrave;nh theo sở th&iacute;ch v&agrave; một phong c&aacute;ch rất ri&ecirc;ng để c&oacute; thể thỏa sức s&aacute;ng tạo?</em></p>\r\n\r\n<p dir="ltr"><em>Bạn muốn chinh phục v&agrave; l&agrave;m chủ Photoshop - một c&ocirc;ng cụ chỉnh sửa tuyệt vời c&oacute; thể thỏa m&atilde;n sự đam m&ecirc; hay trợ gi&uacute;p cho c&ocirc;ng việc của bạn?</em></p>\r\n\r\n<p dir="ltr"><em>Bạn muốn t&igrave;m kiếm một kho&aacute; học thiết kế n&acirc;ng cao ph&ugrave; hợp?</em></p>\r\n\r\n<p dir="ltr"><em><strong>Tuy nhi&ecirc;n,</strong></em></p>\r\n\r\n<p dir="ltr">- Bạn kh&aacute; bỡ ngỡ với Photoshop v&agrave; chưa biết bắt đầu từ đ&acirc;u ?</p>\r\n\r\n<p dir="ltr">- Bạn chưa c&oacute; những kiến thức cơ bản về Photoshop n&ecirc;n cảm thấy rất hoang mang ?</p>\r\n\r\n<p dir="ltr">- Hoặc đơn giản l&agrave; bạn chưa thể t&igrave;m được cho m&igrave;nh một đơn vị học photoshop n&acirc;ng cao&nbsp;uy t&iacute;n, chất lượng với gi&aacute; cả phải chăng ?</p>\r\n\r\n<p dir="ltr"><em><strong>Nhưng bạn đừng qu&aacute; lo lắng,&nbsp;</strong></em></p>\r\n\r\n<p dir="ltr">Bởi kh&oacute;a học&nbsp;<em><strong>Th&agrave;nh thạo Photoshop từ cơ bản đến n&acirc;ng cao</strong></em>&nbsp;được bi&ecirc;n soạn bởi giảng vi&ecirc;n&nbsp;<em><strong>Phạm Thiệp</strong></em>&nbsp;sẽ gi&uacute;p bạn chinh phục Photoshop một c&aacute;ch dễ d&agrave;ng. Bạn sẽ được sở hữu những bộ Plugin gi&uacute;p bạn xử l&yacute; h&igrave;nh ảnh đẹp trong chớp mắt kh&ocirc;ng được c&agrave;i đặt sẵn trong Photoshop, những action gi&uacute;p bạn tạo ra những hiệu ứng chỉ bằng 1 c&uacute; click chuột.</p>\r\n\r\n<p dir="ltr"><em><strong>Lợi &iacute;ch khi tham gia kh&oacute;a học&nbsp;photoshop n&acirc;ng cao</strong></em></p>\r\n\r\n<p dir="ltr">- Bạn sẽ c&oacute; được tất cả c&aacute;c b&agrave;i học b&agrave;i bản v&agrave; chi tiết nhất từ cơ bản đến n&acirc;ng cao c&aacute;c thao t&aacute;c thực hiện trong Photoshop, được hỗ trợ trong suốt kh&oacute;a học, giải đ&aacute;p c&aacute;c thắc mắc</p>\r\n\r\n<p dir="ltr">- V&agrave; điều đặc biệt, kh&oacute;a học sẽ lu&ocirc;n cập nhật những c&ocirc;ng cụ hữu &iacute;ch trong thiết kế, giảng vi&ecirc;n sẽ lu&ocirc;n theo s&aacute;t để hỗ trợ c&aacute;c bạn học tập v&agrave; thực h&agrave;nh một c&aacute;ch tốt nhất.</p>\r\n\r\n<p dir="ltr">- Ngo&agrave;i ra, kh&oacute;a học Mua một lần sở hữu trọn đời. Bạn c&oacute; thể học mọi l&uacute;c, mọi nơi, khi chỉ cần điện thoại hoặc m&aacute;y t&iacute;nh c&oacute; kết nối Internet. Điều n&agrave;y gi&uacute;p bạn chủ động hơn trong việc học, tiết kiệm được nhiều thời gian v&agrave; chi ph&iacute; so với h&igrave;nh thức học Offline tại c&aacute;c trung t&acirc;m.</p>\r\n\r\n<p dir="ltr">Vậy c&ograve;n chần chừ g&igrave;, h&atilde;y nhanh tay đăng k&yacute; kh&oacute;a học&nbsp;<em><strong>&quot;Th&agrave;nh thạo Photoshop từ cơ bản đến n&acirc;ng cao&quot;</strong></em>&nbsp;- Thế giới ảnh trong tầm tay bạn&nbsp;</p>', '2021-02-26 12:10:07', '2021-02-27 09:55:59'),
	(21, 'Trở thành nhà thiết kế chuyên nghiệp với Adobe Photoshop CC 2015', '400000', 'Tạo được kĩ xảo, blend màu cho ảnh một cách bài bản và chuyên nghiệp cùng Adobe Photoshop CC 2015', '300000', '12', '11 giờ 25 phút', '108', 'https://tuhocdohoa.vn/wp-content/uploads/2018/03/H%E1%BB%8Dc-thi%E1%BA%BFt-k%E1%BA%BF-b%E1%BA%B1ng-photoshop-1.jpg', '<h3>Bạn sẽ học được g&igrave;</h3>\r\n\r\n<p>Th&agrave;nh thạo những c&ocirc;ng cụ cơ bản, cần phải biết trong phần mềm Adobe Photoshop</p>\r\n\r\n<p>C&oacute; kĩ năng sửa ảnh ch&acirc;n dung ấn tượng, chuy&ecirc;n nghiệp</p>\r\n\r\n<p>C&oacute; khả năng chỉnh sửa ảnh phong cảnh theo s&aacute;ng tạo &yacute; muốn</p>\r\n\r\n<p>C&oacute; thể tạo ra một giao diện web đẹp kh&ocirc;ng thua k&eacute;m g&igrave; d&acirc;n thiết kế chuy&ecirc;n nghiệp chuy&ecirc;n về web</p>\r\n\r\n<p>Tự tay thiết kế được những&nbsp;banner quảng c&aacute;o &quot;độc&quot; v&agrave; &quot;lạ&quot;</p>\r\n\r\n<p>Biết c&aacute;ch l&agrave;m ra được những bức ảnh động th&uacute; vị chỉ từ Photoshop&nbsp;</p>\r\n\r\n<p>Tạo ra được những hiệu ứng độc đ&aacute;o v&agrave; ấn tượng cho tấm ảnh thiết kế</p>\r\n\r\n<p>Nắm được c&aacute;c nguy&ecirc;n l&yacute; về c&aacute;ch l&agrave;m ảnh v&agrave; tối ưu việc sử dụng Photoshop</p>\r\n\r\n<p>Tạo được nền tảng chắc chắn v&agrave; kỹ năng l&agrave;m việc chuy&ecirc;n nghiệp để trở th&agrave;nh một Freelancer, một Designer xịn s&ograve;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Giới thiệu kh&oacute;a học</h2>\r\n\r\n<p><strong>Bạn l&agrave; ai:</strong></p>\r\n\r\n<p>❓ Bạn l&agrave; người c&oacute; niềm đam m&ecirc; về ảnh v&agrave; muốn tạo ra những tấm ảnh ấn tượng?</p>\r\n\r\n<p>❓ Bạn l&agrave; Designer, Marketer, Freelancer tự do&nbsp;hay đang l&agrave;m những c&ocirc;ng việc m&agrave; kĩ năng chỉnh sửa ảnh l&agrave; y&ecirc;u cầu bắt buộc?</p>\r\n\r\n<p>❓ Bạn vẫn đang đi t&igrave;m cho m&igrave;nh một hướng đi ph&aacute;t triển, hướng&nbsp;học thiết kế&nbsp;để&nbsp;c&oacute; sự&nbsp;s&aacute;ng tạo v&agrave; khả năng thu nhập cao?</p>\r\n\r\n<p>❓ Bạn cần một phần mềm chỉnh sửa ảnh chuy&ecirc;n nghiệp để tạo ra những poster, banner hay thậm ch&iacute; l&agrave; thiết kế giao diện web đẹp mắt, ấn tượng?</p>\r\n\r\n<p>Để giải quyết tất cả c&aacute;c vấn đề tr&ecirc;n, Giảng vi&ecirc;n L&ecirc; Đức Lợi đ&atilde; kết hợp c&ugrave;ng Unica mang đến cho c&aacute;c bạn kh&oacute;a học trực tuyến &quot;<strong>Trở th&agrave;nh nh&agrave; thiết kế chuy&ecirc;n nghiệp với Adobe Photoshop CC 2015&quot;</strong>.</p>\r\n\r\n<p>Với nội dung kh&oacute;a học l&ecirc;n tới 107 b&agrave;i học trong thời lượng 11 giờ 39 ph&uacute;t, giảng vi&ecirc;n L&ecirc; Đức Lợi&nbsp;sẽ đem lại cho bạn đầy đủ tất cả những kiến thức cần thiết về c&ocirc;ng cụ của Photoshop m&agrave; một nh&agrave; thiết kế chỉnh sửa ảnh bắt buộc phải biết:</p>\r\n\r\n<p>+ L&agrave;m quen v&agrave; sử&nbsp;dụng th&agrave;nh thạo c&aacute;c c&ocirc;ng cụ trong photoshop</p>\r\n\r\n<p>+ Nắm bắt được c&aacute;c kiến thức cơ bản về ảnh kĩ thuật số</p>\r\n\r\n<p>+ Tạo được kĩ xảo, blend m&agrave;u cho ảnh một c&aacute;ch b&agrave;i bản v&agrave; chuy&ecirc;n nghiệp</p>\r\n\r\n<p>+ Ứng dụng th&ocirc;ng minh v&agrave; nhanh ch&oacute;ng v&agrave;o c&ocirc;ng việc thiết kế hiệu quả nhất</p>\r\n\r\n<p>Kh&ocirc;ng chỉ c&oacute; vậy kh&oacute;a học c&ograve;n hướng dẫn bạn &aacute;p dụng ngay những kiến thức đ&atilde; học để l&agrave;m những c&ocirc;ng việc cụ thể chứ kh&ocirc;ng chỉ l&agrave; học l&yacute; thuyết su&ocirc;ng.</p>\r\n\r\n<p><strong>Sau kh&oacute;a học bạn ho&agrave;n to&agrave;n c&oacute; thể:</strong></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Tự tay tạo ra những bức ảnh đẹp lung linh v&agrave; chuy&ecirc;n nghiệp&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Tự chỉnh sửa v&agrave; cho ra đời những bức ảnh ch&acirc;n dung tuyệt đẹp</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Chỉnh sửa ảnh phong cảnh theo phong c&aacute;ch v&agrave; sự s&aacute;ng tạo của ri&ecirc;ng m&igrave;nh</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Thiết kế được giao diện web, poster, banner quảng c&aacute;o game theo phong c&aacute;ch đẹp - độc -&nbsp;lạ</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;✔️ Trở th&agrave;nh một Designer, một Freelancer tự do chuy&ecirc;n nghiệp với mức thu nhập h&agrave;ng chục triệu/th&aacute;ng</p>\r\n\r\n<p>Muốn c&oacute; ảnh đẹp phải biết Photoshop! Vậy c&ograve;n chờ g&igrave; nữa&nbsp;h&atilde;y nhanh tay đăng k&iacute; kh&oacute;a học Photoshop &quot;<strong>Trở th&agrave;nh nh&agrave; thiết kế chuy&ecirc;n nghiệp với Adobe Photoshop CC 2015&quot;</strong>&nbsp;th&ocirc;i!</p>', '2021-02-26 12:10:07', '2021-02-27 09:58:41'),
	(22, 'Lập trình Kotlin từ cơ bản đến nâng cao', '390000', 'Khóa học sẽ giúp bạn có được kiến thức toàn diện về ngôn ngữ lập trình Kotlin, phát triển được trên phần mềm java,Native,Web cực đơn giản sau khi học xong khóa này', '290000', '13', '19 giờ 25 phút', '95', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRcHZ9IJrf2XWjjTKKdheH16MjWKFk85He6Gw&usqp=CAU', NULL, '2021-02-26 12:10:07', '2021-02-26 12:10:07'),
	(23, 'Học lập trình TYPESCRIPT – ES6 (JAVASCRIPT) từ đầu', '390000', 'JavaScript giờ trở thành ngôn ngữ lập trình phổ biến nhất thế giới. Trước đây JavaScript không được thiết kế để viết ứng dụng doanh nghiệp lớn. Nếu ứng dụng chỉ vỏn vẹn vài chục dòng code, JavaScript không gây khó khăn gì. Nhưng khi số dòng code lên đến hàng ngàn, rắc rối bắt đầu nảy sinh. Kiểm tra lỗi trở nên khó khăn, sửa chữa code cũng không phải dễ.', '290000', '14', '14 giờ 13 phút', '94', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRcHZ9IJrf2XWjjTKKdheH16MjWKFk85He6Gw&usqp=CAU', NULL, '2021-02-26 12:10:07', '2021-02-26 12:10:07'),
	(24, 'Học jQuery từ cơ bản đến nâng cao', '290000', 'Khóa Học jQuery Từ Cơ Bản Đến Nâng Cao là cách nhanh chóng để vận dụng những tính năng tối ưu của nó vào công việc', '190000', '15', '17 giờ 15 phút', '109', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRcHZ9IJrf2XWjjTKKdheH16MjWKFk85He6Gw&usqp=CAU', NULL, '2021-02-26 12:10:07', '2021-02-26 12:10:07');
